import React, { useState } from 'react';
import ApointmentForm from '../ApointmentForm/ApointmentForm';

const BookingCard = ({booking}) => {
    const [modalIsOpen,setIsOpen] = useState(false);
    function openModal() {
      setIsOpen(true);
    }
   
    function closeModal(){
      setIsOpen(false);
    }
    return (
        <div className="col-md-4 mb-5">
            <div className="card p-3">
                <div className="card-body text-center">
                    <h5 className="card-title text-brand">
                        {booking.subject}
                    </h5>
                    <h6>{booking.visitingHour}</h6>
                    <p>{booking.totalSpace} SPACE AVAILABLE</p>
                    <button className="btn btn-brand">BOOK APPOINTMENT</button>
                    <ApointmentForm></ApointmentForm>
                </div>
            </div>
        </div>
    );
};

export default BookingCard;
